import { TestBed } from '@angular/core/testing';
import { AuthWidgetComponent } from './auth-widget.component';
import { provideHttpClient } from '@angular/common/http';
import { provideHttpClientTesting } from '@angular/common/http/testing';
import { ENVIRONMENT, User } from '@jdw/angular-shared-util';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';
import { BehaviorSubject, of } from 'rxjs';
import { AuthService, UsersService } from '@jdw/angular-shared-data-access';

describe(AuthWidgetComponent.name, () => {
  const mockUser: User = {
    id: 1,
    emailAddress: 'user@jdwkube.com',
    password: 'P@ssw0rd',
    status: 'ACTIVE',
    roles: [],
    profile: {
      id: 1,
      firstName: 'John',
      middleName: 'A',
      lastName: 'Doe',
      birthdate: '1990-01-01',
      userId: 1,
      icon: {
        id: 1,
        profileId: 1,
        icon: '',
        createdByUserId: 1,
        createdTime: '2024-08-09T01:02:34.567+00:00',
        modifiedByUserId: 1,
        modifiedTime: '2024-09-01T10:11:12.000+00:00',
      },
      addresses: [],
      createdByUserId: 1,
      createdTime: '2024-08-09T01:02:34.567+00:00',
      modifiedByUserId: 1,
      modifiedTime: '2024-08-09T01:02:34.567+00:00',
    },
    createdByUserId: 1,
    createdTime: '2024-08-09T01:02:34.567+00:00',
    modifiedByUserId: 1,
    modifiedTime: '2024-08-09T01:02:34.567+00:00',
  };
  const testToken =
    'eyJhbGciOiJIUzUxMiJ9..CwWqeBChnj1Gw2aS8tIlbqaR8Z9Xys0uviUo8XDjd82Z0fwg-QP1MnnXb1th5VJ9FLr0ku5qoe5NSTCUMuP_rg';
  let token$: BehaviorSubject<string | null>;

  beforeEach(() => {
    token$ = new BehaviorSubject<string | null>(null);

    TestBed.configureTestingModule({
      imports: [BrowserAnimationsModule],
      providers: [
        provideHttpClient(),
        provideHttpClientTesting(),
        {
          provide: ENVIRONMENT,
          useValue: {},
        },
        {
          provide: AuthService,
          useValue: {
            token$: token$,
          },
        },
      ],
    }).overrideComponent(AuthWidgetComponent, {
      add: {
        imports: [],
        providers: [],
      },
    });
  });

  it('renders', () => {
    cy.mount(AuthWidgetComponent);
  });

  it('shows login icon when logged out', () => {
    cy.mount(AuthWidgetComponent, {
      providers: [
        {
          provide: AuthService,
          useValue: {
            token$: token$,
            getToken: () => '',
          },
        },
      ],
    });
    token$.next(null);
    cy.getByCy('sign-out-icon').should('exist');
  });

  it('shows person icon when logged in without icon', () => {
    const mockUser: User = {
      id: 1,
      emailAddress: 'user@jdwkube.com',
      password: 'P@ssw0rd',
      status: 'ACTIVE',
      roles: [],
      profile: null,
      createdByUserId: 1,
      createdTime: '2024-08-09T01:02:34.567+00:00',
      modifiedByUserId: 1,
      modifiedTime: '2024-08-09T01:02:34.567+00:00',
    };
    cy.mount(AuthWidgetComponent, {
      providers: [
        {
          provide: AuthService,
          useValue: {
            token$: token$,
            getToken: () => testToken,
            signOut: () => '',
            isTokenExpired: () => false,
            getUserIdFromToken: () => 1,
          },
        },
        {
          provide: UsersService,
          useValue: {
            getUser: (userId: number | string) => of(mockUser),
          },
        },
      ],
    });
    token$.next(testToken);
    cy.getByCy('no-icon-icon').should('exist');
  });

  it('shows account icon when logged in with icon', () => {
    cy.mount(AuthWidgetComponent, {
      providers: [
        {
          provide: AuthService,
          useValue: {
            token$: token$,
            getToken: () => testToken,
            signOut: () => '',
            isTokenExpired: () => false,
            getUserIdFromToken: () => 1,
          },
        },
        {
          provide: UsersService,
          useValue: {
            getUser: (userId: number | string) => of(mockUser),
          },
        },
      ],
    });
    token$.next(testToken);
    cy.getByCy('icon-image').should('exist');
  });

  it('shows correct menu items when logged out', () => {
    cy.mount(AuthWidgetComponent, {
      providers: [
        {
          provide: AuthService,
          useValue: {
            token$: token$,
            getToken: () => '',
            signOut: () => '',
            isTokenExpired: () => false,
            getUserIdFromToken: () => 1,
          },
        },
        {
          provide: UsersService,
          useValue: {
            getUser: (userId: number | string) => of(mockUser),
          },
        },
      ],
    });
    token$.next(null);
    cy.getByCy('auth-widget-button').click();
    cy.getByCy('sign-in-button').should('exist');
    cy.getByCy('sign-in-button').should('exist');
    cy.getByCy('sign-up-button').should('exist');

    cy.getByCy('account-button').should('not.exist');
    cy.getByCy('switch-account-button').should('not.exist');
    cy.getByCy('sign-out-button').should('not.exist');
  });

  it('shows correct menu items when logged in', () => {
    cy.mount(AuthWidgetComponent, {
      providers: [
        {
          provide: AuthService,
          useValue: {
            token$: token$,
            getToken: () => testToken,
            signOut: () => '',
            isTokenExpired: () => false,
            getUserIdFromToken: () => 1,
          },
        },
        {
          provide: UsersService,
          useValue: {
            getUser: (userId: number | string) => of(mockUser),
          },
        },
      ],
    });
    token$.next(testToken);

    cy.getByCy('auth-widget-button').click();
    cy.getByCy('account-button').should('exist');
    cy.getByCy('switch-account-button').should('exist');
    cy.getByCy('sign-out-button').should('exist');

    cy.getByCy('sign-in-button').should('not.exist');
    cy.getByCy('sign-in-button').should('not.exist');
    cy.getByCy('sign-up-button').should('not.exist');
  });
});
